import React, { Component } from 'react';
import Logo from "../img/logo.png";

class PlayingGame extends Component {

    state = {
        showInput: false
    };

    render() {
        const {
            players
        } = this.props;

        const {
            showInput
        } = this.state;

        let playersWithInout = [];
        if (showInput) {
            playersWithInout.concat(
                {showInput: true}
            );
        }
        playersWithInout.concat(players);
        return (
            <section className="content has-players">
                <div className="logo">
                    <img src={Logo} alt=""/>
                </div>

                <section className="add-player">
                    <a href="" className="btn is-tertiary">
                        Add
                    </a>
                </section>

                <ul className="row">
                    {
                        players.map(player => {
                            if (player.showInput) {
                                return (
                                    <input className="player-add-name"/>
                                );
                            } else {
                                return (
                                    <li
                                        data-count={player.lives}
                                        className="col-xs-12 col-md-6">
                                        {player.name}
                                    </li>
                                )
                            }

                        })
                    }
                </ul>
                {/*<div>*/}
                {/*<input/>*/}
                {/*<a*/}
                {/*className="btn is-black"*/}
                {/*>*/}
                {/*ADD*/}
                {/*</a>*/}
                {/*</div>*/}

                <section className="group">
                    <a href="" className="btn is-white">Remove<i/></a>
                    <a href="" className="btn is-black">Next</a>
                    <a href="" className="btn is-white">Add</a>
                </section>
            </section>
        );
    }
};


export default PlayingGame;
